mapper.select:
SELECT a.announcement_id AS id, a.title AS title, u.name AS writerName, a.status AS status, a.created_at AS createdAt FROM announcement a JOIN user u ON u.user_id = a.user_id
AND (LOWER(a.title) LIKE CONCAT('%', LOWER(#{request.keyword}), '%') OR LOWER(a.content) LIKE CONCAT('%', LOWER(#{request.keyword}), '%'))
AND a.status = #{request.status}
ORDER BY a.announcement_id DESC LIMIT #{request.sizeOrDefault()} OFFSET #{request.offset()}

SELECT COUNT(*) FROM announcement a
AND (LOWER(a.title) LIKE CONCAT('%', LOWER(#{request.keyword}), '%') OR LOWER(a.content) LIKE CONCAT('%', LOWER(#{request.keyword}), '%'))
AND a.status = #{request.status}
